function o(){const e=document.getElementById("cookie-banner");!localStorage.getItem("cookies-accepted")&&e&&e.classList.remove("hidden")}function c(){localStorage.setItem("cookies-accepted","true");const e=document.getElementById("cookie-banner");e&&e.classList.add("hidden")}document.addEventListener("DOMContentLoaded",o);window.acceptCookies=c;const t=document.getElementById("mobile-menu-button"),n=document.getElementById("mobile-menu");t&&n&&t.addEventListener("click",()=>{n.classList.toggle("hidden")});
